package TypeCasting_Wrapper;

public class ReferenceToReference1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		  // Reference data type: Object
        Object obj = "Hello, World!"; // Object references a String

        // Typecasting Object to String
        String str = (String) obj;

        // Display the values
        System.out.println("Reference Object: " + obj);
        System.out.println("Reference String: " + str);
 
        
	}

}
