package Variable_declaration;

public class Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// variable declaration

				int student_roll_no;
				String student_name;
				double percent_10;
				float percent_12;
				char grade;                  
				short fees;
				long pin;
				byte age;
				boolean result;
				
				//initialization
				student_roll_no=1;
				student_name="Akash";
				percent_10=70.80;
				percent_12=55.50f;
				grade='A';
				fees=25000;
				pin=413501;
				age=24;
				result=true;
				
				//usage
				
				System.out.println("student roll no= "+student_roll_no);
				
				System.out.println(" name= "+student_name);

				System.out.println("10 th percent of student= "+percent_10);

				System.out.println("12 th percent of student= "+percent_12);

				System.out.println("gread of student= "+grade);

				System.out.println("student fees= "+fees);

				System.out.println("pin code= "+pin);

				System.out.println("age of the student= "+age);

				System.out.println("result= "+result);
				

	}

}
