package loops;

public class While_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Print a to z character using while loop
		
int  i='a';    //declared initialized
		
		while(i<'z')  //give condition
		{
			System.out.println(i);
			i++;                              // increament by 1
		}

	}

}
